def get_ascii_art(category, name): # Function to retrieve ascii art from the Ascii art dictionary.
    return ascii_art_dictionary.get(category, {}).get(name, "Character or item not found.") # Returns the ascii art called upon.

print(get_ascii_art("friendly_characters", "Elira")) # How to call upon the artwork in question

# TEST COMMAND, ONLY CALL FOR MAKING SURE EACH CATEGORY, NAME, AND ART IS PROPERLY STORED. To test, paste - test_all_ascii(ascii_art_dictionary) - and run.

def test_all_ascii(ascii_art_dictionary): # TEST COMMAND, ONLY CALL FOR MAKING SURE EACH CATEGORY, NAME, AND ART IS PROPERLY STORED. To test, paste - test_all_ascii(ascii_art_dictionary) - and run.
    for art_type, characters in ascii_art_dictionary.items(): # This nested for loop goes through each category and prints it all for debugging purposes.
        for character, ascii_art in characters.items():
            print(f"{art_type}: {character}: {ascii_art}")

# End of the test command.

test_all_ascii(ascii_art_dictionary)

